package edp.davinci;

import java.io.UnsupportedEncodingException;

import edp.core.utils.AESUtils;
import edp.core.utils.TokenUtils;

public class Test {
	public static void main(String[] args) throws UnsupportedEncodingException {
    	String decrypt = AESUtils.decrypt("",null);
        System.out.println("1~~~~~~~~~~~~"+decrypt);
        TokenUtils tokenUtils = new TokenUtils();
        String tokenUserName = tokenUtils.getUsername(decrypt);
        String tokenPassword = tokenUtils.getPassword(decrypt);
        System.out.println(tokenUserName);
        System.out.println(tokenPassword);
        
        
        
        decrypt = AESUtils.decrypt("",null);
        System.out.println("1~~~~~~~~~~~~"+decrypt);
        tokenUtils = new TokenUtils();
        tokenUserName = tokenUtils.getUsername(decrypt);
        tokenPassword = tokenUtils.getPassword(decrypt);
        System.out.println(tokenUserName);
        System.out.println(tokenPassword);
    }
}
